function searchPartTypeArrows() {
    $('.part-type-search-field').keydown(function (e) {
        var currentSelected = $('.partcodesearchresult.partcode-selected');
        var prevSelected = null;
        var nextSelected = null;
        var urlRedirect = null;

        switch (e.which) {
            case 38:
                // up
                e.preventDefault();
                currentSelected.removeClass('partcode-selected');
                prevSelected = currentSelected.prev();
                prevSelected.addClass('partcode-selected');

                if (!prevSelected.hasClass('partcode-selected')) {
                    $('#partcodesearchresults .search-title').children().last().addClass('partcode-selected');
                    return;
                }
                currentSelected = prevSelected;
                break;

            case 40:
                // down
                e.preventDefault();
                currentSelected.removeClass('partcode-selected');
                nextSelected = currentSelected.next();
                nextSelected.addClass('partcode-selected');

                if (!nextSelected.hasClass('partcode-selected')) {
                    $('#partcodesearchresults .search-title').children().first().addClass('partcode-selected');
                    return;
                }
                currentSelected = nextSelected;
                break;

            case 13:
                urlRedirect = currentSelected.find('a').attr('href');
                window.location = urlRedirect;
                break;

            default:
                return;
        }

        e.preventDefault();
    });

    $('#partcodesearchresults').on({
        mouseenter: function mouseenter() {
            $(this).addClass('partcode-selected');
        },
        mouseleave: function mouseleave() {
            $(this).removeClass('partcode-selected');
        }
    }, '.partcodesearchresult');
}

