    var selectionTextContainer = $(this).find('.selection-text');
    var item = this;
    var type = $(this).closest('#brands-carousel').length > 0 ? 'desktop' : 'mobile';

    if (!selectedBrandId) return;

    //unbind click event
    $(item).off('click');

    //empty the options container in case it's been used before
    optionsContainer.empty();

    //set the selection text
    setSelectionText(selectionTextContainer, selectedBrandName, 'model');

    //API call to fetch all model-year pairs for the selected make
    $.get({
        url: BASE_PATH + '/api/search/make/' + selectedBrandId,
        crossDomain: true
    }).done(function (data) {
        var modelObjects = data.searchmodels;
        var loop = true;

        lodash.chunk(modelObjects, 5).map(function (chunk, chunkIndex) {
            if (loop) {
                var column = type == 'desktop' ? "<div class='options-column'>" : "<div class='options-column-mobile'>";
                chunk.map(function (model) {
                    column += '<span class="fake-link" data-model-id="' + model.id + '" data-model-name="' + model.name + '">' + model.name + '</span>';
                });

                if (chunkIndex === 3 && modelObjects.length > TOP_COUNT_IN_CAROUSEL - 1) {
                    column += '<a href="' + encodeURI(BASE_PATH + '/cars/' + selectedBrandName) + '" style="margin: 20px 0;">See all models</a>'; //TODO: Enter URL to the page with all the YMMs
                    loop = false;
                }
                column += "</div>";
                optionsContainer.append(column);
            }
        });

        //bind click events on models
        $(item).find('span').on('click', function (e) {
            var selectedModelId = $(this).data('modelId');
            var selectedModelName = $(this).data('modelName');

            $.get({
                url: BASE_PATH + '/api/search/year/' + selectedBrandId + '/' + selectedModelId,
                crossDomain: true
            }).done(function (data) {
                optionsContainer.empty();
                var yearsObjects = data;
                setSelectionText(selectionTextContainer, selectedBrandName, 'year');

                if (yearsObjects.length === 0) {
                    optionsContainer.append("<h2>No years were found for this model.</h2>");
                    return;
                }

                lodash.chunk(yearsObjects, 5).map(function (chunk, chunkIndex) {
                    var column = type == 'desktop' ? "<div class='options-column'>" : "<div class='options-column-mobile'>";

                    chunk.map(function (modelYear) {
                        column += '<a href="' + encodeURI(BASE_PATH + '/cars/' + selectedBrandName + '/' + selectedModelName + '/' + modelYear.year) + '">' + modelYear.year + '</a>';
                    });

                    if (chunkIndex === 3 && yearsObjects.length > TOP_COUNT_IN_CAROUSEL - 1) {
                        column += '<a href="' + encodeURI(BASE_PATH + '/cars/' + selectedBrandName + '/' + selectedModelName) + '" style="margin: 20px 0;">See all years</a>'; //TODO: Enter URL to the page with all the YMMs
                        loop = false;
                    }

                    column += "</div>";
                    optionsContainer.append(column);
                });
            }).fail(function (e) {
                return alert('Oops something went wrong. Please try reloading the page.');
            });
        });
    }).fail(function (e) {
        return alert('Oops something went wrong. Please try reloading the page.');
    });

    $(item).find('.close-button').off('click').on('click', { item: item, type: type }, closeCarouselItem);

    //hide other sibling items
    $(this).fadeOut(300);
    $(this).siblings().fadeOut(300);

    //start carousel item opening animations and transitions
    setTimeout(openCarouselItem.bind(this), 300);
