Handlebars.registerHelper('algoliaPartCodeName', function() {
    if (this._highlightResult.name) {
        return new Handlebars.SafeString(this._highlightResult.name.value);
    }
    return this.name;
});

Handlebars.registerHelper('algoliaPartCodeSynonym', function() {
    if (this._highlightResult.name && this._highlightResult.name.matchLevel != 'none') return;
    for (nm in this._highlightResult.synonyms) {
        var synonym = this._highlightResult.synonyms[nm];
        if (synonym.matchLevel != 'none') {
            return new Handlebars.SafeString(synonym.value);
        }
    }
});
