function YMMSelector($year, $make, $model, year, makeid, ymmid) {
    var my = {};

    function updateyears() {
        var d = $.Deferred()
        d.resolve();
        my.$year.html("<option value=''>e.g. 2016</option>");
        for (var year = 2017; year > 1928; year--) {
            my.$year.append($('<option></option>').val(year).text(year));
        }
        my.$year.combobox();
        return d.promise();
    }
    function updatemakes() {
        $('.wrapper-make-search .combobox-container').remove();
        my.$make.unbind('combobox').removeData();

        if (!my.$year.val()) {
            my.$make.prop('disabled', true);
            my.$make.html("<option value=''>e.g. Toyota</option>");
            my.$make.addClass('hidden');
            $('.wrapper-make-search .input-group').removeClass('hidden');

            if (!my.$make.val()) {
                my.$model.prop('disabled', true);
                my.$model.html("<option value=''>e.g. Corolla</option>");
                my.$model.addClass('hidden');
                $('.wrapper-model-search .input-group').removeClass('hidden');
                $('.wrapper-model-search .combobox-container').remove();
                my.$model.unbind('combobox').removeData();
            }
            return;
        }

        my.$make.prop('disabled', true);
        my.$make.html("<option value=''>Loading Makes</option>");
        my.$make.trigger("change");
        var deferredReady = $.Deferred();
        my.makeDeferredReady = deferredReady;
        $.getJSON('/api/search/make', {'year': my.$year.val()})
            .then(function (data) {
                if (my.makeDeferredReady !== deferredReady) return;
                my.$make.prop('disabled', false);
                my.$make.html("<option value=''>e.g. Toyota</option>");
                var makes = _.sortBy(data, 'name');
                for (var i = 0; i < makes.length; i++) {
                    my.$make.append($('<option></option>').val(makes[i].id).text(makes[i].name));
                }
                my.$make.removeClass('hidden');
                $('.wrapper-make-search .input-group').addClass('hidden');
                my.$make.combobox();
                deferredReady.resolve();
            }).then(function() {
                if (my.$year.val()) {
                    $('.wrapper-make-search input').focus();
                }
            });
        return deferredReady.promise();
    }
    function updatemodels() {
        $('.wrapper-model-search .combobox-container').remove();
        my.$model.unbind('combobox').removeData();

        if (!my.$make.val()) {
            my.$model.prop('disabled', true);
            my.$model.html("<option value=''>e.g. Corolla</option>");
            my.$model.addClass('hidden');
            $('.wrapper-model-search .input-group').removeClass('hidden');
            return;
        }

        my.$model.prop('disabled', true);
        my.$model.html("<option value=''>Loading Models</option>");
        my.$model.trigger("change");
        var deferredReady = $.Deferred();
        my.modelDeferredReady = deferredReady;
        $.getJSON('/api/search/year', {'year': my.$year.val(), 'searchmake': my.$make.val()})
            .then(function (data) {
                if (my.modelDeferredReady !== deferredReady) return;
                my.$model.prop('disabled', false);
                my.$model.html("<option value=''>e.g. Corolla</option>");
                var models = _.sortBy(data, function (d) { return d.searchmodel.name; });
                for (var i = 0; i < models.length; i++) {
                    my.$model.append($('<option></option>').val(models[i].id).text(models[i].searchmodel.name));
                }
                my.$model.removeClass('hidden');
                $('.wrapper-model-search .input-group').addClass('hidden');
                my.$model.combobox();
                deferredReady.resolve();
            })
            .then(function() {
                if (my.$make.val()) {
                    $('.wrapper-model-search input').focus();
                }
            });
        return deferredReady.promise();
    }

    my.setup = function ($year, $make, $model, year, makeid, ymmid) {
        my.$year = $year;
        my.$make = $make;
        my.$model = $model;

        updateyears()
            .then(function () {
                if (year) {
                  my.$year.val(year);
                }
            })
            .then(updatemakes)
            .then(function () {
                if (makeid) {
                    my.$make.val(makeid);
                }
            })
            .then(updatemodels)
            .then(function () {
                if (ymmid) {
                   my.$model.val(ymmid);
                }
            })
            .then(function () {
                $year.change(updatemakes);
                $make.change(updatemodels);
                $model.change(function() {
                    var findPartsButton = $('#home-search-section .search-by-ymm-row .search-by-ymm-button');
                    if (my.$model.val() && findPartsButton) {
                        setTimeout(function() {
                            findPartsButton.focus()
                        }, 200);
                    }
                })
            });
    };

    my.getYMMorShowError = function () {
        if (!my.$year.val()) {
            my.$year.addClass('has-error');
        }
        if (!my.$make.val()) {
            my.$make.addClass('has-error');
        }
        if (!my.$model.val()) {
            my.$model.addClass('has-error');
        } else {
            return my.$model.val();
        }

        function removeErrors() {
            my.$year.removeClass('has-error');
            my.$make.removeClass('has-error');
            my.$model.removeClass('has-error');
        }
        setTimeout(removeErrors, 2000);
    };

    my.setup($year, $make, $model, year, makeid, ymmid);

    return my;
}
;var ShareModal = Backbone.View.extend({
    events: {
        "click .closemodal": "closemodal",
        "submit form": "sendmessage"
    },

    initialize: function(options) {
        this.url = options.url;
        this.urlhash = options.urlhash;
        this.partcode_id = options.partcode_id;
        this.message = options.message;
        this.render();
    },

    render: function() {
        this.$el.html(JST['components/modal.hbs']());
        this.$el.find('.content').html(JST['share.hbs']({
            message: this.message
        }));
        this.$el.find('.modal').modal('show');
    },

    sendmessage: function(e) {
        e.preventDefault();
        this.$el.find('.form-group').removeClass('has-error');
        this.$el.find('.help-block').remove();

        var data = validate.collectFormValues(this.$el.find('form'));

        // Check whether everything is in order
        var constraints = {
            sender: {length: {minimum: 4}, presence: true},
            email: {email: true, presence: true},
            message: {length: {minimum: 5}, presence: true}
        }

        var errors = validate(data, constraints);

        if (errors) {
            for (name in errors) {
                var e = errors[name];
                var el = this.$el.find('[name=' + name + ']');
                el.parent().append($('<span class="help-block">' + e + '</span>'));
                el.closest('.form-group').addClass('has-error');
            }
            return
        }

        // Everything's cool. Show the user we're loading something.
        this.$el.find('.write').addClass('hidden');
        this.$el.find('.modal-footer').addClass('hidden');
        this.$el.find('.sending').removeClass('hidden');

        // now actually do something
        data['url'] = this.url;
        data['urlhash'] = this.urlhash;
        data['partcode'] = {"id": this.partcode_id};
        var self = this;
        $.ajax('/api/share', {
                data: JSON.stringify(data),
                type: 'POST',
                contentType: "application/json;"
            })
            .done(function() {
                self.$el.find('.sending').addClass('hidden');
                self.$el.find('.sent').removeClass('hidden');
                ga('send', 'event', 'submit', 'SharePart');
            })
            .fail(function(response) {
                self.$el.find('.sending').addClass('hidden');
                self.$el.find('.error').removeClass('hidden');
            });
    },

    closemodal: function(e) {
        if (e) e.preventDefault();
        this.$el.find('.modal').modal('hide');
    }
});var SmsOptInModal = Backbone.View.extend({
    show: false,
    events: {
        "click #enroll-sms": 'registerTextNumber'
    },

    initialize: function(options) {
        this.async = options.async;
        var self = this;
        $.ajax('/api/smsoptinpopup', {
            type: 'GET',
            async: self.async,
            contentType: "application/json;",
            success: function (data) {
                self.show = data.show;
                if (data.show) {
                    $.proxy(self, self.render());
                }
            }
        });
    },

    render: function() {
        this.$el.html(JST['components/modal.hbs']());
        this.$el.find('.content').html(JST['smsoptinpopup.hbs']());
        this.$el.find('[name=textnumber]').mask("(999) 999-9999");
        this.$el.find('.modal').modal('show');
        this.$el.find('.modal').on('hidden.bs.modal', this.closemodal);
    },

    closemodal: function() {
        $.ajax('/api/smsoptinpopup', {
            data: JSON.stringify({'enroll': false}),
            type: 'POST',
            contentType: "application/json;",
            success: function(data) {
                if (data == 'OK') {
                    ga('send', 'event', 'submit', 'SMSOptInPopup', false);
                }
            }
        });
    },

    registerTextNumber: function() {
        var textnumber = this.$el.find('[name=textnumber]').val();
        if (!textnumber) {
            this.$el.find('.error-message').removeClass('hidden');
            this.$el.find('.error-message').text('Please enter a valid phone number.');
            return false;
        }

        $.ajax('/api/smsoptinpopup', {
            data: JSON.stringify({'enroll': true, 'textnumber': textnumber}),
            type: 'POST',
            contentType: "application/json;",
            success: function(data) {
                if (data == 'OK') {
                    ga('send', 'event', 'submit', 'SMSOptInPopup', true);
                    var currentUrl = window.location.href;
                    window.location.href = currentUrl;
                }
            }
        });
    }
});
